import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Parse from 'parse';

const Home = () => {
  const navigate = useNavigate();
  const [courses] = useState([
    { 
      id: "linux", 
      title: "Linux Admin", 
      price: "150,000 MMK", 
      img: "https://images.unsplash.com/photo-1629654297299-c8506221ca97", 
      introDes: "Linux သည် server လောက၏ ဘုရင်ဖြစ်သည်။ Enterprise Server များ တည်ဆောက်ပုံကို လက်တွေ့သင်ကြားရမည်။",
      instructorName: "Dr. May Mi Ko Ko",
      instructorImg: "images/919888af5779d83aa33de5790bf7eb72.jpg"
    },
    { 
      id: "cyber", 
      title: "Cyber Security", 
      price: "200,000 MMK", 
      img: "https://images.unsplash.com/photo-1550751827-4bd374c3f58b", 
      introDes: "Security Analyst တစ်ယောက်ဖြစ်ဖို့ လိုအပ်တဲ့ SOC Operations များကို Lab များဖြင့် လက်တွေ့လေ့ကျင့်ရမည်။",
      instructorName: "U Kyaw Naing",
      instructorImg: "images/images (1).jpeg"
    },
    { 
      id: "net", 
      title: "Networking", 
      price: "150,000 MMK", 
      img: "https://images.unsplash.com/photo-1544197150-b99a580bb7a8", 
      introDes: "Network အခြေခံမှစ၍ Router/Switch များ configuration လုပ်ပုံအထိ စနစ်တကျ သင်ကြားပေးမည်။",
      instructorName: "CISCO GURU",
      instructorImg: "https://api.dicebear.com/7.x/avataaars/svg?seed=Max"
    },
    { 
      id: "py_basic", 
      title: "Python Basic", 
      price: "100,000 MMK", 
      img: "https://images.unsplash.com/photo-1526374965328-7f61d4dc18c5", 
      introDes: "Programming လောကထဲ စတင်ဝင်ရောက်မည့်သူများအတွက် အခြေခံအကျဆုံးသင်တန်းဖြစ်သည်။",
      instructorName: "PY MASTER",
      instructorImg: "https://api.dicebear.com/7.x/avataaars/svg?seed=Caleb"
    },
    { 
      id: "py_adv", 
      title: "Python Adv", 
      price: "200,000 MMK", 
      img: "https://images.unsplash.com/photo-1515879218367-8466d910aaa4", 
      introDes: "Python ကို အဆင့်မြင့်အသုံးပြုပြီး Web Application များ ရေးသားလိုသူများအတွက်ဖြစ်သည်။",
      instructorName: "ADV DEV",
      instructorImg: "https://api.dicebear.com/7.x/avataaars/svg?seed=Tyler"
    },
    { 
      id: "hacking", 
      title: "World Hacking", 
      price: "150,000 MMK", 
      img: "https://images.unsplash.com/photo-1563986768609-322da13575f3", 
      introDes: "Ethical Hacking ကို စနစ်တကျလေ့လာပြီး Bug Bounty ရှာဖွေလိုသူများအတွက် အကောင်းဆုံးဖြစ်သည်။",
      instructorName: "DARK KNIGHT",
      instructorImg: "https://api.dicebear.com/7.x/avataaars/svg?seed=Shadow"
    }
  ]);

  const [showStatusModal, setShowStatusModal] = useState(false);
  const [checkPhone, setCheckPhone] = useState('');
  const [statusResult, setStatusResult] = useState(null);

  useEffect(() => {
    const savedEnrollment = localStorage.getItem("approvedEnrollment");
    if (savedEnrollment) {
      const data = JSON.parse(savedEnrollment);
      navigate(`/dashboard/${data.courseId}`);
    }
  }, [navigate]);

  const checkMyStatus = async () => {
    if (!checkPhone) return;
    try {
      const query = new Parse.Query("Enrollments");
      query.equalTo("phoneNumber", checkPhone);
      query.descending("createdAt");
      const result = await query.first();
      if (result) {
        const status = result.get("status");
        const courseId = result.get("courseId");
        const title = result.get("courseTitle");
        setStatusResult({ title, status, courseId });
        if (status === 'approved') {
          localStorage.setItem("approvedEnrollment", JSON.stringify({ courseId, phoneNumber: checkPhone }));
        }
      } else { alert("စာရင်းမရှိသေးပါဗျာ။"); }
    } catch (e) { alert(e.message); }
  };

  return (
    <div style={styles.pageWrapper}>
      {/* Dynamic Animation Injector */}
      <style>
        {`
          @keyframes marquee {
            0% { transform: translateX(0); }
            100% { transform: translateX(-50%); }
          }
          .marquee-container:hover .marquee-content {
            animation-play-state: paused;
          }
        `}
      </style>

      <nav style={styles.nav}>
        <h2 style={styles.logo}>CYBER SERVICE ACADEMY (CSA)</h2>
        <button onClick={() => setShowStatusModal(true)} style={styles.checkStatusBtn}>CHECK STATUS</button>
      </nav>

      {/* AUTO LOOP SLIDER SECTION */}
      <div style={styles.heroSection}>
        <div className="marquee-container" style={styles.marqueeContainer}>
          <div className="marquee-content" style={styles.marqueeContent}>
            {/* Render 2 sets for seamless loop */}
            {[...courses, ...courses].map((course, index) => (
              <div key={index} style={styles.loopCard}>
                <div style={{...styles.cardImg, backgroundImage: `url(${course.img})`}}></div>
                <div style={styles.cardOverlay}>
                  <div style={styles.instructorMini}>
                    <img src={course.instructorImg} style={styles.miniImg} alt="pro" />
                    <span>{course.instructorName}</span>
                  </div>
                  <h3 style={styles.cardTitle}>{course.title}</h3>
                  <p style={styles.cardPrice}>{course.price}</p>
                  <button onClick={() => navigate(`/?id=${course.id}`)} style={styles.joinBtn}>VIEW MISSION</button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* FOOTER */}
      <footer style={styles.footer}>
         <p>© 2024 CYBER SERVICE ACADEMY (CSA). All Rights Reserved.</p>
      </footer>

      {/* STATUS MODAL */}
      {showStatusModal && (
        <div style={styles.modalOverlay} onClick={() => setShowStatusModal(false)}>
          <div style={styles.modalContent} onClick={e => e.stopPropagation()}>
            <h3 style={{ color: '#00ff41' }}>CHECK MISSION STATUS</h3>
            <input style={styles.input} placeholder="ENTER PHONE NUMBER" onChange={e => setCheckPhone(e.target.value)} />
            <button style={styles.statusBtn} onClick={checkMyStatus}>CHECK NOW</button>
            {statusResult && (
              <div style={{marginTop: '15px', borderTop: '1px solid #233554', paddingTop: '10px'}}>
                 <p style={{color: '#fff'}}>{statusResult.title}: <b style={{color: '#00ff41'}}>{statusResult.status}</b></p>
                 {statusResult.status === 'approved' && <button onClick={() => navigate(`/dashboard/${statusResult.courseId}`)} style={styles.statusBtn}>ENTER</button>}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

