import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Parse from 'parse';

const Home = () => {
  const navigate = useNavigate();
  const [courses, setCourses] = useState([
    { 
      id: "linux", 
      title: "Linux Admin", 
      price: "150,000 MMK", 
      duration: "2 Months", 
      req: "Basic Computer,less than RAM 8 And 256GB", 
      vidId: "yv_vPAtE_kQ", 
      img: "https://images.unsplash.com/photo-1629654297299-c8506221ca97", 
      detail: "Master Linux CLI, Server Management, and Security Auditing.", 
      introDes: "Linux á€žá€Šá€º server á€œá€±á€¬á€€á á€˜á€¯á€›á€„á€ºá€–á€¼á€…á€ºá€žá€Šá€ºá‹ Enterprise Server á€™á€»á€¬á€¸ á€á€Šá€ºá€†á€±á€¬á€€á€ºá€•á€¯á€¶á€€á€­á€¯ á€œá€€á€ºá€á€½á€±á€·á€žá€„á€ºá€€á€¼á€¬á€¸á€›á€™á€Šá€ºá‹",
      instructorName: "MR. LINUX",
      instructorImg: "public/images/919888af5779d83aa33de5790bf7eb72.jpg"
    },
    { 
      id: "cyber", 
      title: "Cyber Security", 
      price: "200,000 MMK", 
      duration: "2 Months", 
      req: "Network Basics,Basic Computer,less than RAM 8 And 256GB", 
      vidId: "Z3_6qJ0pGno", 
      img: "https://images.unsplash.com/photo-1550751827-4bd374c3f58b", 
      detail: "Learn Threat Hunting, SOC Analysis, and Incident Response.", 
      introDes: "Security Analyst á€á€…á€ºá€šá€±á€¬á€€á€ºá€–á€¼á€…á€ºá€–á€­á€¯á€· á€œá€­á€¯á€¡á€•á€ºá€á€²á€· SOC Operations á€™á€»á€¬á€¸á€€á€­á€¯ Lab á€™á€»á€¬á€¸á€–á€¼á€„á€·á€º á€œá€€á€ºá€á€½á€±á€·á€œá€±á€·á€€á€»á€„á€·á€ºá€›á€™á€Šá€ºá‹",
      instructorName: "AGENT X",
      instructorImg: "https://api.dicebear.com/7.x/avataaars/svg?seed=Aneka"
    },
    { 
      id: "net", 
      title: "Networking", 
      price: "150,000 MMK", 
      duration: "2 Months", 
      req: "Basic Computer,less than RAM 8 And 256GB", 
      vidId: "09Z_yK_hS3k", 
      img: "https://images.unsplash.com/photo-1544197150-b99a580bb7a8", 
      detail: "Deep dive into CCNA level Networking and Protocols.", 
      introDes: "Network á€¡á€á€¼á€±á€á€¶á€™á€¾á€…á Router/Switch á€™á€»á€¬á€¸ configuration á€œá€¯á€•á€ºá€•á€¯á€¶á€¡á€‘á€­ á€…á€”á€…á€ºá€á€€á€» á€žá€„á€ºá€€á€¼á€¬á€¸á€•á€±á€¸á€™á€Šá€ºá‹",
      instructorName: "CISCO GURU",
      instructorImg: "https://api.dicebear.com/7.x/avataaars/svg?seed=Max"
    },
    { 
      id: "py_basic", 
      title: "Python Basic", 
      price: "100,000 MMK", 
      duration: "1.5 Months", 
      req: "Logic,Basic Computer,less than RAM 8 And 256GB", 
      vidId: "mX4Z0D_vL_A", 
      img: "https://images.unsplash.com/photo-1526374965328-7f61d4dc18c5", 
      detail: "Fundamentals of Programming and Automation.", 
      introDes: "Programming á€œá€±á€¬á€€á€‘á€² á€…á€á€„á€ºá€á€„á€ºá€›á€±á€¬á€€á€ºá€™á€Šá€·á€ºá€žá€°á€™á€»á€¬á€¸á€¡á€á€½á€€á€º á€¡á€á€¼á€±á€á€¶á€¡á€€á€»á€†á€¯á€¶á€¸á€žá€„á€ºá€á€”á€ºá€¸á€–á€¼á€…á€ºá€žá€Šá€ºá‹",
      instructorName: "PY MASTER",
      instructorImg: "https://api.dicebear.com/7.x/avataaars/svg?seed=Caleb"
    },
    { 
      id: "py_adv", 
      title: "Python Adv", 
      price: "200,000 MMK", 
      duration: "2 Months", 
      req: "Python Basic", 
      vidId: "X_9e6XmS-Y0", 
      img: "https://images.unsplash.com/photo-1515879218367-8466d910aaa4", 
      detail: "Object Oriented Programming and Web Frameworks.", 
      introDes: "Python á€€á€­á€¯ á€¡á€†á€„á€·á€ºá€™á€¼á€„á€·á€ºá€¡á€žá€¯á€¶á€¸á€•á€¼á€¯á€•á€¼á€®á€¸ Web Application á€™á€»á€¬á€¸ á€›á€±á€¸á€žá€¬á€¸á€œá€­á€¯á€žá€°á€™á€»á€¬á€¸á€¡á€á€½á€€á€ºá€–á€¼á€…á€ºá€žá€Šá€ºá‹",
      instructorName: "ADV DEV",
      instructorImg: "https://api.dicebear.com/7.x/avataaars/svg?seed=Tyler"
    },
    { 
      id: "hacking", 
      title: "World Hacking", 
      price: "150,000 MMK", 
      duration: "4 Months", 
      req: "Linux/Network", 
      vidId: "f9p233_rMSc", 
      img: "https://images.unsplash.com/photo-1563986768609-322da13575f3", 
      detail: "Real-world Pentesting and Exploitation.", 
      introDes: "Ethical Hacking á€€á€­á€¯ á€…á€”á€…á€ºá€á€€á€»á€œá€±á€·á€œá€¬á€•á€¼á€®á€¸ Bug Bounty á€›á€¾á€¬á€–á€½á€±á€œá€­á€¯á€žá€°á€™á€»á€¬á€¸á€¡á€á€½á€€á€º á€¡á€€á€±á€¬á€„á€ºá€¸á€†á€¯á€¶á€¸á€–á€¼á€…á€ºá€žá€Šá€ºá‹",
      instructorName: "DARK KNIGHT",
      instructorImg: "https://api.dicebear.com/7.x/avataaars/svg?seed=Shadow"
    }
  ]);

  const [selectedCourse, setSelectedCourse] = useState(null);
  const [showEnrollForm, setShowEnrollForm] = useState(false);
  const [showStatusModal, setShowStatusModal] = useState(false);

  const [studentName, setStudentName] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [selectedPayment, setSelectedPayment] = useState('kpay');
  const [receiptFile, setReceiptFile] = useState(null);
  const [receiptPreview, setReceiptPreview] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const [checkPhone, setCheckPhone] = useState('');
  const [statusResult, setStatusResult] = useState(null);

  useEffect(() => {
    const savedEnrollment = localStorage.getItem("approvedEnrollment");
    const params = new URLSearchParams(window.location.search);
    const stayOnHome = params.get("stay");

    if (savedEnrollment && !stayOnHome) {
      const data = JSON.parse(savedEnrollment);
      navigate(`/dashboard/${data.courseId}`);
    }
  }, [navigate]);

  const payments = {
    kpay: { name: "KPay", acc: "09455690715(DNZT)", logo: "/images/unnamed.png" },
    wave: { name: "Wave Money", acc: "09953716690(UKMB)", logo: "/images/images.jpeg" },
    ayapay: { name: "AYA Pay", acc: "09953716690(UKMB)", logo: "/images/ayapay-feature.jpg" },
    visa: { name: "Visa Card", acc: "Not Avaiable ", logo: "https://upload.wikimedia.org/wikipedia/commons/thumb/5/5e/Visa_Inc._logo.svg/2560px-Visa_Inc._logo.svg.png" }
  };

  const handleEnrollClick = (course) => { setSelectedCourse(course); setShowEnrollForm(true); };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) { setReceiptFile(file); setReceiptPreview(URL.createObjectURL(file)); }
  };

  const handleSubmitMission = async () => {
    if (!studentName || !phoneNumber || !receiptFile) { alert("á€¡á€á€»á€€á€ºá€¡á€œá€€á€ºá€¡á€¬á€¸á€œá€¯á€¶á€¸ á€–á€¼á€Šá€·á€ºá€…á€½á€€á€ºá€•á€±á€¸á€•á€«á‹"); return; }
    setIsSubmitting(true);
    try {
      const Enroll = Parse.Object.extend("Enrollments");
      const enroll = new Enroll();
      const parseFile = new Parse.File("receipt.jpg", receiptFile);
      await parseFile.save();
      enroll.set("studentName", studentName);
      enroll.set("phoneNumber", phoneNumber);
      enroll.set("courseId", selectedCourse.id);
      enroll.set("courseTitle", selectedCourse.title);
      enroll.set("paymentMethod", selectedPayment);
      enroll.set("receipt", parseFile);
      enroll.set("status", "pending");
      await enroll.save();
      alert("Mission Request Sent! ðŸš€ áá… á€™á€­á€”á€…á€ºá€á€”á€·á€ºá€…á€±á€¬á€„á€·á€ºá€†á€­á€¯á€„á€ºá€¸á€•á€±á€¸á€•á€«á‹");
      setShowEnrollForm(false); setSelectedCourse(null); setReceiptFile(null); setReceiptPreview(null);
    } catch (error) { alert(error.message); } finally { setIsSubmitting(false); }
  };

  const checkMyStatus = async () => {
    if (!checkPhone) return;
    try {
      const query = new Parse.Query("Enrollments");
      query.equalTo("phoneNumber", checkPhone);
      query.descending("createdAt");
      const result = await query.first();
      if (result) {
        const status = result.get("status");
        const courseId = result.get("courseId");
        const title = result.get("courseTitle");
        setStatusResult({ title, status, courseId });
        if (status === 'approved') {
          localStorage.setItem("approvedEnrollment", JSON.stringify({ courseId, phoneNumber: checkPhone }));
        }
      } else { alert("á€’á€®á€–á€¯á€”á€ºá€¸á€”á€¶á€•á€«á€á€ºá€”á€²á€· á€…á€¬á€›á€„á€ºá€¸á€žá€½á€„á€ºá€¸á€‘á€¬á€¸á€á€¬ á€™á€›á€¾á€­á€žá€±á€¸á€•á€«á€˜á€°á€¸á‹"); }
    } catch (e) { alert(e.message); }
  };

  const handleNext = () => { const n = [...courses]; n.push(n.shift()); setCourses(n); };
  const handlePrev = () => { const n = [...courses]; n.unshift(n.pop()); setCourses(n); };

  return (
