                    <button style={styles.abortBtn} onClick={() => setSelectedCourse(null)}>ABORT</button>
                    <button style={styles.enrollBtn} onClick={() => setShowEnrollForm(true)}>JOIN MISSION</button>
                  </div>
                </>
              ) : (
                <>
                  <h3 style={{ color: '#00ff41', textAlign: 'center', marginBottom: '10px' }}>REGISTRATION</h3>
                  <input style={styles.input} placeholder="NAME (á€¡á€™á€Šá€º)" onChange={e => setStudentName(e.target.value)} />
                  <input style={styles.input} placeholder="PHONE (á€–á€¯á€”á€ºá€¸á€”á€¶á€•á€«á€á€º)" onChange={e => setPhoneNumber(e.target.value)} />
                  <div style={styles.paymentFlex}>
                    {Object.keys(payments).map(key => (
                      <div key={key} onClick={() => setSelectedPayment(key)} 
                           style={{...styles.payIconBox, borderColor: selectedPayment === key ? '#00ff41' : '#233554'}}>
                        <img src={payments[key].logo} style={styles.payLogoImg} alt={key} />
                      </div>
                    ))}
                  </div>
                  <div style={styles.accDisplay}>
                    <p style={{margin: 0, fontSize: '11px', color: '#8892b0'}}>TRANSFER TO ({payments[selectedPayment].name})</p>
                    <h4 style={{margin: '5px 0', color: '#00ff41', letterSpacing: '1px'}}>{payments[selectedPayment].acc}</h4>
                  </div>
                  <label style={styles.uploadArea}>
                    <input type="file" hidden onChange={handleFileChange} accept="image/*" />
                    {!receiptPreview ? <div style={{color: '#00ff41', fontSize: '12px'}}>ðŸ“¸ UPLOAD RECEIPT</div> : <img src={receiptPreview} style={{height: '80px'}} alt="Preview" />}
                  </label>
                  <button style={styles.enrollBtn} onClick={handleSubmitMission} disabled={isSubmitting}>
                    {isSubmitting ? "TRANSMITTING..." : "SUBMIT MISSION"}
                  </button>
                  <button style={{...styles.abortBtn, border: 'none'}} onClick={() => setShowEnrollForm(false)}>BACK</button>
                </>
              )}
            </div>
          </div>
        </div>
      )}

      {showStatusModal && (
        <div style={styles.modalOverlay} onClick={() => { setShowStatusModal(false); setStatusResult(null); }}>
          <div style={styles.modalContent} onClick={e => e.stopPropagation()}>
            <div style={styles.modalMain}>
              <h3 style={{ color: '#00ff41' }}>CHECK STATUS</h3>
              <input style={styles.input} placeholder="ENTER PHONE NUMBER" onChange={e => setCheckPhone(e.target.value)} />
              <button style={styles.enrollBtn} onClick={checkMyStatus}>CHECK NOW</button>
              {statusResult && (
                <div style={styles.statusBox}>
                   <p style={{color: '#fff'}}>Course: {statusResult.title}</p>
                   <p style={{color: '#fff'}}>Status: <b style={{color: statusResult.status === 'pending' ? '#ffcc00' : '#00ff41'}}>{statusResult.status.toUpperCase()}</b></p>
                   {statusResult.status === 'approved' && (
                     <button 
                       onClick={() => navigate(`/dashboard/${statusResult.courseId}`)} 
