import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  const initialCourses = [
    { id: "linux", title: "Linux Admin", img: "https://images.unsplash.com/photo-1629654297299-c8506221ca97?q=80&w=1000&auto=format&fit=crop", detail: "Master Linux CLI, Server Management, and Security Auditing." },
    { id: "cyber", title: "Cyber Security", img: "https://images.unsplash.com/photo-1550751827-4bd374c3f58b?q=80&w=1000&auto=format&fit=crop", detail: "Learn Threat Hunting, SOC Analysis, and Incident Response." },
    { id: "net", title: "Networking", img: "https://images.unsplash.com/photo-1544197150-b99a580bb7a8?q=80&w=1000&auto=format&fit=crop", detail: "CCNA level Networking, Protocols, and Firewall Configuration." },
    { id: "py_basic", title: "Python Basic", img: "https://images.unsplash.com/photo-1526374965328-7f61d4dc18c5?q=80&w=1000&auto=format&fit=crop", detail: "Fundamentals of Programming, Automation, and Scripting." },
    { id: "py_adv", title: "Python Advanced", img: "https://images.unsplash.com/photo-1515879218367-8466d910aaa4?q=80&w=1000&auto=format&fit=crop", detail: "Deep dive into OOP, Web Frameworks, and Data Analysis." },
    { id: "hacking", title: "World Hacking", img: "https://images.unsplash.com/photo-1563986768609-322da13575f3?q=80&w=1000&auto=format&fit=crop", detail: "Real-world Pentesting, Bug Bounty, and Exploitation Techniques." }
  ];

  const [courses, setCourses] = useState(initialCourses);
  const [selectedCourse, setSelectedCourse] = useState(null);

  // Next function
  const handleNext = () => {
    const newItems = [...courses];
    newItems.push(newItems.shift());
    setCourses(newItems);
  };

  // Prev function
  const handlePrev = () => {
    const newItems = [...courses];
    newItems.unshift(newItems.pop());
    setCourses(newItems);
  };

  // Card ကို နှိပ်ရင် ရှေ့ကို ခေါ်မယ့် logic
  const handleCardClick = (index) => {
    if (index === 1) return; // လက်ရှိ Main card ဆိုရင် ဘာမှမလုပ်ဘူး

    if (index > 1) {
      // ညာဘက်က ကတ်တွေကို နှိပ်ရင် Next သွားမယ်
      handleNext();
    } else if (index === 0) {
      // ဘယ်ဘက်က ကတ် (ရှိခဲ့ရင်) ကို နှိပ်ရင် Prev သွားမယ်
      handlePrev();
    }
  };

  return (
    <div style={styles.body}>
      <nav style={styles.nav}>
        <h2 style={{ color: '#00ff41', margin: 0 }}>CYBER_SHIELD</h2>
        <div>
          <Link to="/login" style={styles.navLink}>STUDENT LOGIN</Link>
          <Link to="/register" style={styles.regBtn}>JOIN NOW</Link>
        </div>
      </nav>

      <div style={styles.container}>
        <div style={styles.slide}>
          {courses.map((course, index) => (
            <div 
              key={course.id}
              onClick={() => handleCardClick(index)} // ဒီမှာ Click event ထည့်လိုက်တယ်
              style={{
                ...styles.item,
                backgroundImage: `url(${course.img})`,
                opacity: index >= 6 ? 0 : 1,
                display: index === 0 ? 'block' : 'inline-block',
                width: index < 2 ? '100%' : '190px',
                height: index < 2 ? '100%' : '280px',
                top: index < 2 ? '0' : '70%',
                left: index < 2 ? '0' : `${50 + (index-1) * 210}px`,
                zIndex: index < 2 ? 1 : 10,
                borderRadius: index < 2 ? '0' : '20px',
                position: 'absolute',
                transition: '0.8s cubic-bezier(0.4, 0, 0.2, 1)', // Animation ပို smooth ဖြစ်အောင်
                cursor: index >= 2 ? 'pointer' : 'default', // Card လေးတွေကို နှိပ်လို့ရမှန်း သိအောင်
                border: index >= 2 ? '1px solid rgba(0, 255, 65, 0.3)' : 'none'
              }}
            >
              <div style={{ ...styles.content, display: index === 1 ? 'block' : 'none' }}>
                <div style={styles.name}>{course.title}</div>
                <div style={styles.des}>{course.detail}</div>
                <button 
                   style={styles.seeMoreBtn} 
                   onClick={(e) => {
                     e.stopPropagation(); // Card click event ကို မသွားအောင် တားလိုက်တာ
                     setSelectedCourse(course);
                   }}
                >
                  MISSION BRIEFING
                </button>
              </div>
            </div>
          ))}
        </div>

        {/* Navigation Buttons (ရှိနေရင်လည်း မမှားဘူး) */}
        <div style={styles.buttonContainer}>
          <button style={styles.navBtn} onClick={handlePrev}>❮</button>
          <button style={styles.navBtn} onClick={handleNext}>❯</button>
        </div>
      </div>

      {/* Modal Section (Briefing) */}
      {selectedCourse && (
        <div style={styles.modalOverlay} onClick={() => setSelectedCourse(null)}>
          <div style={styles.modalContent} onClick={e => e.stopPropagation()}>
            <h2 style={{ color: '#00ff41' }}>[ MISSION: {selectedCourse.title} ]</h2>
            <hr style={{ borderColor: '#233554', margin: '15px 0' }} />
            <p style={{ lineHeight: '1.8', fontSize: '15px' }}>{selectedCourse.detail}</p>
            <div style={{ marginTop: '30px', display: 'flex', gap: '15px' }}>
                <Link to="/register" style={styles.regBtn}>ENROLL NOW</Link>
                <button style={styles.closeBtn} onClick={() => setSelectedCourse(null)}>CLOSE</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// ... styles တွေက အရင်အတိုင်းပဲမို့ မပြောင်းလဲပါဘူး ...
const styles = {
  body: { backgroundColor: '#0a192f', height: '100vh', overflow: 'hidden', fontFamily: 'monospace', color: '#ccd6f6' },
  nav: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '20px 50px', background: 'rgba(10, 25, 47, 0.8)', position: 'fixed', width: '100%', zIndex: 1000 },
  navLink: { color: '#ccd6f6', textDecoration: 'none', marginRight: '20px' },
  regBtn: { padding: '10px 25px', border: '1px solid #00ff41', color: '#00ff41', textDecoration: 'none', borderRadius: '5px', fontWeight: 'bold' },
  container: { position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', width: '1100px', height: '600px' },
  slide: { width: '100%', height: '100%', position: 'relative' },
  item: { backgroundPosition: 'center', backgroundSize: 'cover' },
  content: { position: 'absolute', top: '50%', left: '80px', transform: 'translate(0, -50%)', width: '450px', zIndex: 20 },
  name: { fontSize: '45px', fontWeight: 'bold', color: '#00ff41', textTransform: 'uppercase' },
  des: { margin: '20px 0', fontSize: '16px' },
  seeMoreBtn: { padding: '12px 25px', border: 'none', cursor: 'pointer', background: '#00ff41', color: '#0a192f', fontWeight: 'bold' },
  buttonContainer: { position: 'absolute', bottom: '30px', left: '100px', display: 'flex', gap: '20px', zIndex: 100 },
  navBtn: { width: '50px', height: '50px', borderRadius: '50%', border: '2px solid #00ff41', background: 'rgba(0, 255, 65, 0.1)', color: '#00ff41', cursor: 'pointer' },
  modalOverlay: { position: 'fixed', top: 0, left: 0, width: '100%', height: '100%', background: 'rgba(10, 25, 47, 0.9)', display: 'flex', justifyContent: 'center', alignItems: 'center', zIndex: 2000 },
  modalContent: { background: '#112240', padding: '40px', borderRadius: '15px', border: '1px solid #00ff41', maxWidth: '500px', width: '90%' },
  closeBtn: { background: 'transparent', border: '1px solid #ff4b2b', color: '#ff4b2b', padding: '10px 20px', borderRadius: '5px', cursor: 'pointer' }
};

export default Home;
