import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Parse from 'parse';

const Admin = () => {
  const [isAdmin, setIsAdmin] = useState(false);
  const [adminPass, setAdminPass] = useState('');
  const [courseId, setCourseId] = useState('linux');
  const [formData, setFormData] = useState({ instructor: '', videoLink: '' });

  // Tabs: 'pending' | 'approved' | 'users'
  const [activeTab, setActiveTab] = useState('pending');
  const [dataList, setDataList] = useState([]); // ဒီကောင်က List အားလုံးကို သိမ်းမှာ
  const [loading, setLoading] = useState(false);

  const checkPass = () => {
    if (adminPass === "") setIsAdmin(true);
    else alert("Wrong Password! ❌");
  };

  // Database ထဲက ရှိသမျှ အကုန်ဆွဲထုတ်တဲ့ Function
  const fetchData = async () => {
    setLoading(true);
    try {
      let query;
      if (activeTab === 'users') {
        query = new Parse.Query(Parse.User); 
      } else {
        query = new Parse.Query("Enrollments");
        query.equalTo("status", activeTab);
      }

      query.descending("createdAt"); // အသစ်ဆုံးလူကို အပေါ်ဆုံးမှာထားမယ်
      const results = await query.find();

      // ဒီနေရာမှာ အရင်ကောင်တွေရော အခုကောင်တွေရော အကုန် List ထဲထည့်လိုက်တာ
      setDataList(results); 
    } catch (error) {
      console.error("Fetch Error:", error);
    } finally {
      setLoading(false);
    }
  };

  // Tab ပြောင်းတိုင်း သို့မဟုတ် Admin login ဝင်တိုင်း အကုန်ပြန်ဆွဲမယ်
  useEffect(() => {
    if (isAdmin) {
      fetchData();

      // Live Query setup
      let subscription;
      const setupLiveQuery = async () => {
        const query = new Parse.Query(activeTab === 'users' ? Parse.User : "Enrollments");
        subscription = await query.subscribe();

        // အသစ်ဝင်လာရင် list ကို refresh လုပ်ပြီး အကုန်ပြန်ပြမယ်
        subscription.on('create', () => {
          fetchData(); 
        });

        subscription.on('update', () => {
          fetchData();
        });

        subscription.on('enter', () => {
          fetchData();
        });
      };

      setupLiveQuery();
      return () => { if(subscription) subscription.unsubscribe(); };
    }
  }, [isAdmin, activeTab]);

  const handleApprove = async (objectId) => {
    try {
      const query = new Parse.Query("Enrollments");
      const enrollment = await query.get(objectId);
      enrollment.set("status", "approved");
      await enrollment.save();
      alert("AGENT ACTIVATED! ✅");
      fetchData(); // Approve ပြီးရင် စာရင်းပြန်ဆွဲမယ်
    } catch (err) { alert(err.message); }
  };

  // ... (handleUpdate logic က အရင်အတိုင်းပဲ)
  const handleUpdate = async (e) => {
    e.preventDefault();
    const Course = Parse.Object.extend("Classes");
    const query = new Parse.Query(Course);
    query.equalTo("courseId", courseId);
    try {
      let obj = await query.first();
      if (!obj) { alert("Class not found!"); return; }
      obj.set("instructor", formData.instructor);
      obj.set("videoLink", formData.videoLink);
      await obj.save();
      alert("Updated! ✅");
    } catch (err) { alert(err.message); }
  };

  if (!isAdmin) return (
    <div style={styles.loginOverlay}>
      <h2 style={{color: '#00ff41'}}>[ COMMAND CENTER LOGIN ]</h2>
      <input type="password" placeholder="PASSWORD" onChange={e => setAdminPass(e.target.value)} style={styles.loginInput} />
      <button onClick={checkPass} style={styles.loginBtn}>ENTER</button>
    </div>
  );

  return (
    <div style={styles.adminBody}>
      <div style={styles.header}>
        <h1>ADMIN DASHBOARD</h1>
        <Link to="/add-class" style={styles.addClassBtn}>+ ADD CLASS</Link>
      </div>

      <div style={styles.gridContainer}>
        {/* Update Form Section */}
        <section style={styles.section}>
          <h3 style={{color: '#86efac'}}>UPDATE_CLASS</h3>
          <form onSubmit={handleUpdate} style={styles.form}>
            <select onChange={e => setCourseId(e.target.value)} style={styles.input}>
              <option value="linux">Linux Admin</option>
              <option value="cyber">Cyber Security</option>
              <option value="hacking">World Hacking</option>
              <option value="py_basic">Python Basic</option>
            </select>
            <input placeholder="Instructor" onChange={e => setFormData({...formData, instructor: e.target.value})} style={styles.input} />
            <input placeholder="Video Link ID" onChange={e => setFormData({...formData, videoLink: e.target.value})} style={styles.input} />
            <button type="submit" style={styles.updateBtn}>UPDATE</button>
          </form>
        </section>

        {/* LIST SECTION (USERS & ENROLLMENTS) */}
        <section style={styles.section}>
          <div style={styles.tabHeader}>
            <button onClick={() => setActiveTab('pending')} style={activeTab === 'pending' ? styles.activeTab : styles.tab}>PENDING</button>
            <button onClick={() => setActiveTab('approved')} style={activeTab === 'approved' ? styles.activeTab : styles.tab}>APPROVED</button>
            <button onClick={() => setActiveTab('users')} style={activeTab === 'users' ? styles.activeTab : styles.tab}>ALL_USERS</button>
          </div>

          <div style={styles.listContainer}>
            {loading && dataList.length === 0 ? (
              <p>ACCESSING DATABASE...</p>
            ) : (
              <div style={styles.scrollArea}>
                {dataList.map((item, index) => (
                  <div key={item.id} style={styles.card}>
                    <div style={{display: 'flex', gap: '15px', alignItems: 'center'}}>
                      <div style={styles.indexNumber}>{index + 1}</div>
                      <div>
                        <div style={{color: '#00ff41', fontWeight: 'bold'}}>
                          {item.get('username') || item.get('studentName')}
                        </div>
                        <div style={{fontSize: '12px', color: '#8892b0'}}>
                          {activeTab === 'users' ? item.get('email') : `Phone: ${item.get('phoneNumber')}`}
                        </div>
                        <div style={{fontSize: '10px', color: '#444'}}>
                          Joined: {item.createdAt.toLocaleString()}
                        </div>
                      </div>
                    </div>
                    {activeTab === 'pending' && (
                      <button onClick={() => handleApprove(item.id)} style={styles.approveBtn}>APPROVE</button>
                    )}
                  </div>
                ))}
              </div>
            )}
            <div style={{marginTop: '10px', fontSize: '12px', color: '#555'}}>
              Total Count: {dataList.length}
            </div>
          </div>
        </section>
      </div>
    </div>
  );
};

