  buttonContainer: { position: 'absolute', bottom: '15%', left: '80px', display: 'flex', gap: '10px', zIndex: 100 },
  navBtn: { width: '45px', height: '40px', background: '#112240', color: '#00ff41', border: '1px solid #00ff41', cursor: 'pointer' },

  introContainer: { padding: '100px 5%', background: '#0a192f' },
  sectionTitle: { textAlign: 'center', color: '#00ff41', marginBottom: '60px' },
