import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Parse from 'parse';

const Home = () => {
  const navigate = useNavigate();
  const [courses, setCourses] = useState([
    { id: "linux", title: "Linux Admin", price: "55,000 MMK", duration: "2 Months", req: "Basic Computer", vidId: "yv_vPAtE_kQ", img: "https://images.unsplash.com/photo-1629654297299-c8506221ca97", detail: "Master Linux CLI, Server Management, and Security Auditing.", introDes: "Linux ·Äû·Ää·Ä∫ server ·Äú·Ä±·Ä¨·ÄÄ·Åè ·Äò·ÄØ·Äõ·ÄÑ·Ä∫·Äñ·Äº·ÄÖ·Ä∫·Äû·Ää·Ä∫·Åã Enterprise Server ·Äô·Äª·Ä¨·Ä∏ ·Äê·Ää·Ä∫·ÄÜ·Ä±·Ä¨·ÄÄ·Ä∫·Äï·ÄØ·Ä∂·ÄÄ·Ä≠·ÄØ ·Äú·ÄÄ·Ä∫·Äê·ÄΩ·Ä±·Ä∑·Äû·ÄÑ·Ä∫·ÄÄ·Äº·Ä¨·Ä∏·Äõ·Äô·Ää·Ä∫·Åã" },
    { id: "cyber", title: "Cyber Security", price: "75,000 MMK", duration: "3 Months", req: "Network Basics", vidId: "Z3_6qJ0pGno", img: "https://images.unsplash.com/photo-1550751827-4bd374c3f58b", detail: "Learn Threat Hunting, SOC Analysis, and Incident Response.", introDes: "Security Analyst ·Äê·ÄÖ·Ä∫·Äö·Ä±·Ä¨·ÄÄ·Ä∫·Äñ·Äº·ÄÖ·Ä∫·Äñ·Ä≠·ÄØ·Ä∑ ·Äú·Ä≠·ÄØ·Ä°·Äï·Ä∫·Äê·Ä≤·Ä∑ SOC Operations ·Äô·Äª·Ä¨·Ä∏·ÄÄ·Ä≠·ÄØ Lab ·Äô·Äª·Ä¨·Ä∏·Äñ·Äº·ÄÑ·Ä∑·Ä∫ ·Äú·ÄÄ·Ä∫·Äê·ÄΩ·Ä±·Ä∑·Äú·Ä±·Ä∑·ÄÄ·Äª·ÄÑ·Ä∑·Ä∫·Äõ·Äô·Ää·Ä∫·Åã" },
    { id: "net", title: "Networking", price: "45,000 MMK", duration: "2 Months", req: "None", vidId: "09Z_yK_hS3k", img: "https://images.unsplash.com/photo-1544197150-b99a580bb7a8", detail: "Deep dive into CCNA level Networking and Protocols.", introDes: "Network ·Ä°·ÄÅ·Äº·Ä±·ÄÅ·Ä∂·Äô·Äæ·ÄÖ·Åç Router/Switch ·Äô·Äª·Ä¨·Ä∏ configuration ·Äú·ÄØ·Äï·Ä∫·Äï·ÄØ·Ä∂·Ä°·Äë·Ä≠ ·ÄÖ·Äî·ÄÖ·Ä∫·Äê·ÄÄ·Äª ·Äû·ÄÑ·Ä∫·ÄÄ·Äº·Ä¨·Ä∏·Äï·Ä±·Ä∏·Äô·Ää·Ä∫·Åã" },
    { id: "py_basic", title: "Python Basic", price: "40,000 MMK", duration: "1.5 Months", req: "Logic", vidId: "mX4Z0D_vL_A", img: "https://images.unsplash.com/photo-1526374965328-7f61d4dc18c5", detail: "Fundamentals of Programming and Automation.", introDes: "Programming ·Äú·Ä±·Ä¨·ÄÄ·Äë·Ä≤ ·ÄÖ·Äê·ÄÑ·Ä∫·Äù·ÄÑ·Ä∫·Äõ·Ä±·Ä¨·ÄÄ·Ä∫·Äô·Ää·Ä∑·Ä∫·Äû·Ä∞·Äô·Äª·Ä¨·Ä∏·Ä°·Äê·ÄΩ·ÄÄ·Ä∫ ·Ä°·ÄÅ·Äº·Ä±·ÄÅ·Ä∂·Ä°·ÄÄ·Äª·ÄÜ·ÄØ·Ä∂·Ä∏·Äû·ÄÑ·Ä∫·Äê·Äî·Ä∫·Ä∏·Äñ·Äº·ÄÖ·Ä∫·Äû·Ää·Ä∫·Åã" },
    { id: "py_adv", title: "Python Adv", price: "60,000 MMK", duration: "2 Months", req: "Python Basic", vidId: "X_9e6XmS-Y0", img: "https://images.unsplash.com/photo-1515879218367-8466d910aaa4", detail: "Object Oriented Programming and Web Frameworks.", introDes: "Python ·ÄÄ·Ä≠·ÄØ ·Ä°·ÄÜ·ÄÑ·Ä∑·Ä∫·Äô·Äº·ÄÑ·Ä∑·Ä∫·Ä°·Äû·ÄØ·Ä∂·Ä∏·Äï·Äº·ÄØ·Äï·Äº·ÄÆ·Ä∏ Web Application ·Äô·Äª·Ä¨·Ä∏ ·Äõ·Ä±·Ä∏·Äû·Ä¨·Ä∏·Äú·Ä≠·ÄØ·Äû·Ä∞·Äô·Äª·Ä¨·Ä∏·Ä°·Äê·ÄΩ·ÄÄ·Ä∫·Äñ·Äº·ÄÖ·Ä∫·Äû·Ää·Ä∫·Åã" },
    { id: "hacking", title: "World Hacking", price: "95,000 MMK", duration: "4 Months", req: "Linux/Network", vidId: "f9p233_rMSc", img: "https://images.unsplash.com/photo-1563986768609-322da13575f3", detail: "Real-world Pentesting and Exploitation.", introDes: "Ethical Hacking ·ÄÄ·Ä≠·ÄØ ·ÄÖ·Äî·ÄÖ·Ä∫·Äê·ÄÄ·Äª·Äú·Ä±·Ä∑·Äú·Ä¨·Äï·Äº·ÄÆ·Ä∏ Bug Bounty ·Äõ·Äæ·Ä¨·Äñ·ÄΩ·Ä±·Äú·Ä≠·ÄØ·Äû·Ä∞·Äô·Äª·Ä¨·Ä∏·Ä°·Äê·ÄΩ·ÄÄ·Ä∫ ·Ä°·ÄÄ·Ä±·Ä¨·ÄÑ·Ä∫·Ä∏·ÄÜ·ÄØ·Ä∂·Ä∏·Äñ·Äº·ÄÖ·Ä∫·Äû·Ää·Ä∫·Åã" }
  ]);

  const [selectedCourse, setSelectedCourse] = useState(null);
  const [showEnrollForm, setShowEnrollForm] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Form States
  const [studentName, setStudentName] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [email, setEmail] = useState('');
  const [selectedPayment, setSelectedPayment] = useState('kpay');
  const [receiptFile, setReceiptFile] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const payments = {
    kpay: { name: "KPay", acc: "09 123 456 789", logo: "https://i.ibb.co/L9H8W7L/kpay.png" },
    wave: { name: "Wave Money", acc: "09 987 654 321", logo: "https://i.ibb.co/R2t9W0L/wave.png" },
    ayapay: { name: "AYA Pay", acc: "09 444 555 666", logo: "https://i.ibb.co/B2vGkP8/ayapay.png" },
    visa: { name: "Visa Card", acc: "4231 **** **** 8888", logo: "https://i.ibb.co/Xz9tPqL/visa.png" }
  };

  useEffect(() => {
    const currentUser = Parse.User.current();
    if (currentUser) setIsLoggedIn(true);
  }, []);

  const handleEnrollClick = (course) => {
    if (!isLoggedIn) {
      alert("Please login first to enroll!");
      navigate('/login');
    } else {
      setSelectedCourse(course);
      setShowEnrollForm(true);
    }
  };

  const handleFileChange = (e) => {
    if (e.target.files[0]) setReceiptFile(e.target.files[0]);
  };

  const handleSubmitMission = async () => {
    if (!studentName || !phoneNumber || !receiptFile) {
      alert("Please fill all required fields and upload receipt!");
      return;
    }

    setIsSubmitting(true);
    try {
      const Enrollment = Parse.Object.extend("Enrollments");
      const enroll = new Enrollment();

      const parseFile = new Parse.File("receipt.jpg", receiptFile);
      await parseFile.save();

      enroll.set("studentName", studentName);
      enroll.set("phoneNumber", phoneNumber);
      enroll.set("email", email);
      enroll.set("courseId", selectedCourse.id);
      enroll.set("courseTitle", selectedCourse.title);
      enroll.set("paymentMethod", selectedPayment);
      enroll.set("receipt", parseFile);
      enroll.set("status", "pending"); // Admin approve ·Äï·Ä±·Ä∏·Äõ·Äî·Ä∫ ·ÄÖ·Ä±·Ä¨·ÄÑ·Ä∑·Ä∫·ÄÜ·Ä≠·ÄØ·ÄÑ·Ä∫·Ä∏
      enroll.set("user", Parse.User.current());

      await enroll.save();
      alert("Mission Request Sent! üöÄ ·ÄÖ·Ä≠·ÄÖ·ÄÖ·Ä∫·Äî·Ä±·Äû·Ä±·Ä¨·ÄÄ·Äº·Ä±·Ä¨·ÄÑ·Ä∑·Ä∫ ·ÅÅ·ÅÖ ·Äô·Ä≠·Äî·ÄÖ·Ä∫·ÄÅ·Äî·Ä∑·Ä∫·ÄÖ·Ä±·Ä¨·ÄÑ·Ä∑·Ä∫·ÄÜ·Ä≠·ÄØ·ÄÑ·Ä∫·Ä∏·Äï·Ä±·Ä∏·Äï·Ä´·Åã");
      setShowEnrollForm(false);
      setSelectedCourse(null);
    } catch (error) {
      alert(error.message);
    } finally {
      setIsSubmitting(false);
    }
  };

  // Slider Logic
  const handleNext = () => { const n = [...courses]; n.push(n.shift()); setCourses(n); };
  const handlePrev = () => { const n = [...courses]; n.unshift(n.pop()); setCourses(n); };

  return (
    <div style={styles.pageWrapper}>
      {/* SECTION 1: SLIDER */}
      <div style={styles.heroSection}>
        <nav style={styles.nav}>
          <h2 style={{ color: '#00ff41', margin: 0 }}>2 CYBER_SHIELD</h2>
          <div style={styles.navRight}>
            {!isLoggedIn ? (
              <Link to="/login" style={styles.loginBtn}>LOGIN</Link>
            ) : (
              <Link to="/dashboard" style={styles.joinBtn}>DASHBOARD</Link>
            )}
          </div>
        </nav>

        <div style={styles.sliderContainer}>
          <div style={styles.slide}>
            {courses.map((course, index) => (
              <div 
                key={course.id}
                onClick={() => index >= 2 && handleNext()}
                style={{
                  ...styles.item,
                  backgroundImage: `url(${course.img})`,
                  width: index < 2 ? '100%' : '140px',
                  height: index < 2 ? '100%' : '200px',
                  left: index < 2 ? '0' : `calc(40% + ${(index - 2) * 150}px)`,
                  top: index < 2 ? '0' : '65%',
                  zIndex: index < 2 ? 1 : 10,
                  transition: '0.8s ease-in-out'
                }}
              >
                {index === 1 && (
                  <div style={styles.content}>
                    <div style={styles.name}>{course.title}</div>
                    <button style={styles.missionBtn} onClick={() => setSelectedCourse(course)}>MISSION BRIEFING</button>
                  </div>
                )}
              </div>
            ))}
          </div>
          <div style={styles.buttonContainer}>
            <button style={styles.navBtn} onClick={handlePrev}>‚ùÆ</button>
            <button style={styles.navBtn} onClick={handleNext}>‚ùØ</button>
          </div>
        </div>
      </div>

      {/* SECTION 2: INTRO */}
      <div style={styles.introContainer}>
        <h2 style={styles.sectionTitle}>[ EXPLORE MISSIONS ]</h2>
        {courses.map((course, idx) => (
          <div key={course.id} style={{...styles.introRow, flexDirection: idx % 2 === 0 ? 'row' : 'row-reverse'}}>
            <div style={styles.introImageSide}><img src={course.img} alt={course.title} style={styles.introImg} /></div>
            <div style={styles.introTextSide}>
              <h3 style={{ color: '#00ff41', fontSize: '2rem' }}>0{idx + 1}. {course.title}</h3>
              <p style={styles.introPara}>{course.introDes}</p>
              <div style={styles.miniStats}>Duration: {course.duration} | Price: {course.price}</div>
              <button onClick={() => handleEnrollClick(course)} style={styles.learnMoreBtn}>ENROLL MISSION</button>
            </div>
          </div>
        ))}
      </div>

      {/* FOOTER */}
      <footer style={styles.footer}>
        <div style={styles.footerContent}>
          <div style={styles.footerBrand}>
            <h2 style={{ color: '#00ff41' }}>2 CYBER_SHIELD</h2>
            <p>Training the next generation of Cyber Warriors.</p>
          </div>
          <div style={styles.footerSocial}>
            <h4 style={{ color: '#00ff41' }}>FOLLOW_US</h4>
            <div style={{ display: 'flex', gap: '20px' }}>
              <a href="#" style={styles.socialLink}><img src="https://cdn-icons-png.flaticon.com/512/733/733547.png" width="20" /> Facebook</a>
              <a href="#" style={styles.socialLink}><img src="https://cdn-icons-png.flaticon.com/512/2111/2111646.png" width="20" /> Telegram</a>
              <a href="mailto:admin@cyber.com" style={styles.socialLink}><img src="https://cdn-icons-png.flaticon.com/512/732/732200.png" width="20" /> Email</a>
            </div>
          </div>
        </div>
      </footer>

      {/* MODAL & PAYMENT FORM */}
      {(selectedCourse || showEnrollForm) && (
        <div style={styles.modalOverlay} onClick={() => { setSelectedCourse(null); setShowEnrollForm(false); }}>
          <div style={styles.modalContent} onClick={e => e.stopPropagation()}>
            {!showEnrollForm ? (
              <div style={styles.modalMain}>
                <h2 style={{ color: '#00ff41' }}>[ MISSION: {selectedCourse.title} ]</h2>
                <iframe width="100%" height="250" src={`https://www.youtube.com/embed/${selectedCourse.vidId}`} style={{ border: 'none', borderRadius: '8px' }}></iframe>
                <p style={{ color: '#8892b0' }}>{selectedCourse.detail}</p>
                <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '10px' }}>
                  <button style={styles.abortBtn} onClick={() => setSelectedCourse(null)}>ABORT</button>
                  <button style={styles.enrollBtn} onClick={() => setShowEnrollForm(true)}>JOIN MISSION</button>
                </div>
              </div>
            ) : (
              <div style={styles.modalMain}>
                <h3 style={{ color: '#00ff41' }}>MISSION REGISTRATION</h3>
                <input style={styles.input} placeholder="NAME" onChange={e => setStudentName(e.target.value)} />
                <input style={styles.input} placeholder="PHONE" onChange={e => setPhoneNumber(e.target.value)} />

                <h4 style={{ color: '#8892b0', marginTop: '20px' }}>SELECT PAYMENT METHOD:</h4>
                <div style={styles.paymentGrid}>
                  {Object.keys(payments).map(key => (
                    <div 
                      key={key} 
                      onClick={() => setSelectedPayment(key)}
                      style={{...styles.payCard, borderColor: selectedPayment === key ? '#00ff41' : '#233554'}}
                    >
                      <img src={payments[key].logo} width="30" height="30" />
                      <span>{payments[key].name}</span>
                    </div>
                  ))}
                </div>

                <div style={styles.accInfo}>
                  <p>TRANSFER TO: <span style={{ color: '#00ff41' }}>{payments[selectedPayment].acc}</span></p>
                </div>

                <label style={styles.uploadBtn}>
                  UPLOAD RECEIPT (Image)
                  <input type="file" hidden onChange={handleFileChange} accept="image/*" />
                </label>
                {receiptFile && <p style={{ color: '#00ff41', fontSize: '11px' }}>‚úÖ {receiptFile.name}</p>}

                <div style={{ display: 'flex', gap: '10px', marginTop: '20px' }}>
                  <button style={styles.abortBtn} onClick={() => setShowEnrollForm(false)}>CANCEL</button>
                  <button style={styles.enrollBtn} onClick={handleSubmitMission} disabled={isSubmitting}>
                    {isSubmitting ? "TRANSMITTING..." : "SUBMIT MISSION"}
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

