import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Parse from 'parse';

const AdminPortal = () => {
  // --- PART 1: ADD COURSE STATES ---
  const [courseData, setCourseData] = useState({
    courseId: '', title: '', instructor: '', zoomLink: '', videoLink: '', schedule: ''
  });
  const [addStatus, setAddStatus] = useState('');

  // --- PART 2: STUDENT ENROLLMENTS STATES ---
  const [requests, setRequests] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetchEnrollments();
  }, []);

  const fetchEnrollments = async () => {
    setIsLoading(true);
    try {
      const query = new Parse.Query("Enrollments");
      query.descending("createdAt");
      const results = await query.find();
      setRequests(results);
    } catch (error) {
      console.error("Error fetching enrollments:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleApprove = async (id) => {
    try {
      const query = new Parse.Query("Enrollments");
      const object = await query.get(id);
      object.set("status", "approved");
      await object.save();
      alert("Mission Approved! âœ… á€€á€»á€±á€¬á€„á€ºá€¸á€žá€¬á€¸ Status á€…á€…á€ºá€›á€„á€º Approved á€•á€¼á€”á€±á€•á€«á€•á€¼á€®á‹");
      fetchEnrollments();
    } catch (error) {
      alert(error.message);
    }
  };

  const handleSubmitCourse = async (e) => {
    e.preventDefault();
    setAddStatus('Processing...');
    const Course = Parse.Object.extend("Classes");
    const newCourse = new Course();
    try {
      newCourse.set("courseId", courseData.courseId.toLowerCase());
      newCourse.set("title", courseData.title);
      newCourse.set("instructor", courseData.instructor);
      newCourse.set("videoLink", courseData.videoLink);
      newCourse.set("schedule", courseData.schedule);
      await newCourse.save();
      setAddStatus('New Class Added Successfully! âœ…');
      setCourseData({ courseId: '', title: '', instructor: '', zoomLink: '', videoLink: '', schedule: '' });
    } catch (error) { setAddStatus('Error: ' + error.message); }
  };

  return (
    <div style={{ backgroundColor: '#0a192f', minHeight: '100vh', color: '#00ff41', padding: '40px', fontFamily: 'monospace' }}>

      {/* SECTION 1: STUDENT REQUESTS (APPROVE á€•á€±á€¸á€›á€”á€º) */}
      <div style={{ maxWidth: '1000px', margin: '0 auto 60px auto' }}>
        <h1 style={{ borderLeft: '4px solid #00ff41', paddingLeft: '15px' }}>ðŸš€ PENDING MISSIONS (Enrollments)</h1>
        <p style={{ color: '#8892b0' }}>á€€á€»á€±á€¬á€„á€ºá€¸á€žá€¬á€¸á€™á€»á€¬á€¸á€•á€­á€¯á€·á€‘á€¬á€¸á€žá€±á€¬ á€„á€½á€±á€œá€½á€²á€•á€¼á€±á€…á€¬á€™á€»á€¬á€¸á€€á€­á€¯ á€…á€…á€ºá€†á€±á€¸á€›á€”á€º</p>

        {isLoading ? <p>Loading Data...</p> : (
          <div style={{ overflowX: 'auto' }}>
            <table style={styles.table}>
              <thead>
                <tr style={styles.tableHeader}>
                  <th>NAME</th>
                  <th>PHONE</th>
                  <th>COURSE</th>
                  <th>RECEIPT</th>
                  <th>STATUS</th>
                  <th>ACTION</th>
                </tr>
              </thead>
              <tbody>
                {requests.map((req) => (
                  <tr key={req.id} style={styles.tableRow}>
                    <td>{req.get("studentName")}</td>
                    <td>{req.get("phoneNumber")}</td>
                    <td>{req.get("courseTitle")}</td>
                    <td>
                      <a href={req.get("receipt").url()} target="_blank" rel="noreferrer" style={{color: '#00ff41'}}>VIEW IMG</a>
                    </td>
                    <td style={{ color: req.get("status") === 'pending' ? '#ffcc00' : '#00ff41' }}>
                      {req.get("status").toUpperCase()}
                    </td>
                    <td>
                      {req.get("status") === 'pending' && (
                        <button onClick={() => handleApprove(req.id)} style={styles.approveBtn}>APPROVE</button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      <hr style={{ borderColor: '#233554', margin: '40px 0' }} />

      {/* SECTION 2: ADD NEW COURSE */}
      <div style={{ maxWidth: '600px', margin: '0 auto' }}>
        <h1>âž• ADD NEW CLASS</h1>
        <form onSubmit={handleSubmitCourse} style={styles.form}>
          <input placeholder="COURSE ID (e.g., linux)" style={styles.input} value={courseData.courseId} onChange={(e) => setCourseData({...courseData, courseId: e.target.value})} required />
          <input placeholder="COURSE TITLE" style={styles.input} value={courseData.title} onChange={(e) => setCourseData({...courseData, title: e.target.value})} required />
          <input placeholder="INSTRUCTOR" style={styles.input} value={courseData.instructor} onChange={(e) => setCourseData({...courseData, instructor: e.target.value})} />
          <input placeholder="YOUTUBE VIDEO ID" style={styles.input} value={courseData.videoLink} onChange={(e) => setCourseData({...courseData, videoLink: e.target.value})} />
          <input placeholder="SCHEDULE" style={styles.input} value={courseData.schedule} onChange={(e) => setCourseData({...courseData, schedule: e.target.value})} />
          <button type="submit" style={styles.btn}>SAVE TO DATABASE</button>
        </form>
        {addStatus && <div style={styles.statusMsg}>{addStatus}</div>}
      </div>
    </div>
  );
};

const styles = {
  table: { width: '100%', borderCollapse: 'collapse', marginTop: '20px', background: '#112240' },
  tableHeader: { borderBottom: '2px solid #00ff41', textAlign: 'left', color: '#8892b0' },
  tableRow: { borderBottom: '1px solid #233554' },
  approveBtn: { background: '#00ff41', border: 'none', padding: '5px 10px', cursor: 'pointer', fontWeight: 'bold', fontSize: '11px' },
  form: { display: 'flex', flexDirection: 'column', gap: '15px', background: '#112240', padding: '30px', borderRadius: '5px' },
  input: { padding: '12px', background: '#0a192f', border: '1px solid #233554', color: '#fff' },
  btn: { padding: '15px', background: '#00ff41', color: '#0a192f', fontWeight: 'bold', border: 'none', cursor: 'pointer' },
  statusMsg: { marginTop: '20px', padding: '10px', border: '1px solid #00ff41', textAlign: 'center' }
};

export default AdminPortal;
