import React, { useState } from 'react';
import Parse from 'parse';

const Home = () => {
  const [courses] = useState([
    { id: "linux", title: "Linux Admin", price: "55,000 MMK", vidId: "yv_vPAtE_kQ", img: "https://images.unsplash.com/photo-1629654297299-c8506221ca97", introDes: "Linux ·Äû·Ää·Ä∫ server ·Äú·Ä±·Ä¨·ÄÄ·Åè ·Äò·ÄØ·Äõ·ÄÑ·Ä∫·Äñ·Äº·ÄÖ·Ä∫·Äû·Ää·Ä∫·Åã" },
    { id: "cyber", title: "Cyber Security", price: "75,000 MMK", vidId: "Z3_6qJ0pGno", img: "https://images.unsplash.com/photo-1550751827-4bd374c3f58b", introDes: "Security Analyst ·Äê·ÄÖ·Ä∫·Äö·Ä±·Ä¨·ÄÄ·Ä∫·Äñ·Äº·ÄÖ·Ä∫·Äñ·Ä≠·ÄØ·Ä∑ SOC Operations ·ÄÄ·Ä≠·ÄØ ·Äú·Ä±·Ä∑·Äú·Ä¨·Äõ·Äô·Ää·Ä∫·Åã" },
    { id: "net", title: "Networking", price: "45,000 MMK", vidId: "09Z_yK_hS3k", img: "https://images.unsplash.com/photo-1544197150-b99a580bb7a8", introDes: "Network ·Ä°·ÄÅ·Äº·Ä±·ÄÅ·Ä∂·Äô·Äæ·ÄÖ·Åç Router/Switch configuration ·Ä°·Äë·Ä≠·Åã" },
  ]);

  const [selectedCourse, setSelectedCourse] = useState(null);
  const [showEnrollForm, setShowEnrollForm] = useState(false);
  const [showStatusModal, setShowStatusModal] = useState(false);

  // Form States
  const [studentName, setStudentName] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [selectedPayment, setSelectedPayment] = useState('kpay');
  const [receiptFile, setReceiptFile] = useState(null);
  const [receiptPreview, setReceiptPreview] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Status Check States
  const [checkPhone, setCheckPhone] = useState('');
  const [statusResult, setStatusResult] = useState(null);

  const payments = {
    kpay: { name: "KPay", acc: "09 123 456 789", logo: "https://image.winudf.com/v2/image1/Y29tLmtiengucGF5X2ljb25fMTU1NDE1NjM0M18wNjg/icon.png?w=170&fakeurl=1" },
    wave: { name: "Wave Money", acc: "09 987 654 321", logo: "https://www.wavemoney.com.mm/favicon.ico" }
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) { 
      setReceiptFile(file); 
      setReceiptPreview(URL.createObjectURL(file)); 
    }
  };

  const handleSubmitMission = async () => {
    if (!studentName || !phoneNumber || !receiptFile) { 
      alert("·Ä°·ÄÅ·Äª·ÄÄ·Ä∫·Ä°·Äú·ÄÄ·Ä∫·Ä°·Ä¨·Ä∏·Äú·ÄØ·Ä∂·Ä∏ ·Äñ·Äº·Ää·Ä∑·Ä∫·ÄÖ·ÄΩ·ÄÄ·Ä∫·Äï·Ä±·Ä∏·Äï·Ä´·Åã"); 
      return; 
    }
    setIsSubmitting(true);
    try {
      const Enroll = Parse.Object.extend("Enrollments");
      const enroll = new Enroll();
      const parseFile = new Parse.File("receipt.jpg", receiptFile);
      await parseFile.save();
      enroll.set("studentName", studentName);
      enroll.set("phoneNumber", phoneNumber);
      enroll.set("courseTitle", selectedCourse.title);
      enroll.set("paymentMethod", selectedPayment);
      enroll.set("receipt", parseFile);
      enroll.set("status", "pending");
      await enroll.save();
      alert("·Äû·ÄÑ·Ä∫·Äê·Äî·Ä∫·Ä∏·Ä°·Äï·Ä∫·Äî·Äæ·Ä∂·Äô·Äæ·ÄØ ·Äê·ÄÑ·Ä∫·Äï·Äº·Äï·Äº·ÄÆ·Ä∏·Äï·Ä´·Äï·Äº·ÄÆ·Åã ·ÅÅ·ÅÖ ·Äô·Ä≠·Äî·ÄÖ·Ä∫·ÄÅ·Äî·Ä∑·Ä∫ ·ÄÖ·Ä±·Ä¨·ÄÑ·Ä∑·Ä∫·Äï·Ä±·Ä∏·Äï·Ä´·Åã");
      setShowEnrollForm(false); 
      setSelectedCourse(null);
    } catch (error) { 
      alert(error.message); 
    } finally { 
      setIsSubmitting(false); 
    }
  };

  const checkMyStatus = async () => {
    if (!checkPhone) return;
    try {
      const query = new Parse.Query("Enrollments");
      query.equalTo("phoneNumber", checkPhone);
      query.descending("createdAt");
      const result = await query.first();
      if (result) {
        setStatusResult({ 
          title: result.get("courseTitle"), 
          status: result.get("status") 
        });
      } else {
        alert("·Äí·ÄÆ·Äñ·ÄØ·Äî·Ä∫·Ä∏·Äî·Ä∂·Äï·Ä´·Äê·Ä∫·Äî·Ä≤·Ä∑ ·ÄÖ·Ä¨·Äõ·ÄÑ·Ä∫·Ä∏·Äû·ÄΩ·ÄÑ·Ä∫·Ä∏·Äë·Ä¨·Ä∏·Äê·Ä¨ ·Äô·Äõ·Äæ·Ä≠·Äû·Ä±·Ä∏·Äï·Ä´·Åã");
      }
    } catch (e) { alert(e.message); }
  };

  return (
    <div style={styles.pageWrapper}>
      <nav style={styles.nav}>
        <h2 style={styles.logo}>2 CYBER_SHIELD</h2>
        <button onClick={() => setShowStatusModal(true)} style={styles.checkBtn}>CHECK STATUS</button>
      </nav>

      <div style={styles.introContainer}>
        <h2 style={styles.sectionTitle}>[ EXPLORE MISSIONS ]</h2>
        <div style={styles.missionGrid}>
          {courses.map((course) => (
            <div key={course.id} style={styles.missionShapeCard}>
              <div style={{...styles.imgWrap, backgroundImage: `url(${course.img})`}}></div>
              <div style={styles.missionText}>
                <h3 style={{ color: '#00ff41', margin: '5px 0' }}>{course.title}</h3>
                <p style={{ fontSize: '12px', color: '#8892b0' }}>{course.introDes}</p>
                <div style={{color: '#00ff41', marginBottom: '15px'}}>{course.price}</div>
                <button onClick={() => { setSelectedCourse(course); setShowEnrollForm(true); }} style={styles.shapeBtn}>ENROLL</button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* ENROLL MODAL */}
      {showEnrollForm && (
        <div style={styles.modalOverlay} onClick={() => setShowEnrollForm(false)}>
          <div style={styles.modalContent} onClick={e => e.stopPropagation()}>
            <div style={styles.modalMain}>
              <h3 style={{ color: '#00ff41', textAlign: 'center' }}>REGISTRATION</h3>
              <input style={styles.input} placeholder="NAME (·Ä°·Äô·Ää·Ä∫)" onChange={e => setStudentName(e.target.value)} />
              <input style={styles.input} placeholder="PHONE (·Äñ·ÄØ·Äî·Ä∫·Ä∏·Äî·Ä∂·Äï·Ä´·Äê·Ä∫)" onChange={e => setPhoneNumber(e.target.value)} />

              <div style={styles.paymentFlex}>
                {Object.keys(payments).map(key => (
                  <div key={key} onClick={() => setSelectedPayment(key)} 
                       style={{...styles.payIconBox, borderColor: selectedPayment === key ? '#00ff41' : '#233554'}}>
                    <img src={payments[key].logo} style={styles.payLogoImg} alt={key} />
                  </div>
                ))}
              </div>

              <div style={styles.accDisplay}>
                <p style={{margin: 0, fontSize: '11px'}}>{payments[selectedPayment].name}</p>
                <h4 style={{margin: '5px 0', color: '#00ff41'}}>{payments[selectedPayment].acc}</h4>
              </div>

              <label style={styles.uploadArea}>
                <input type="file" hidden onChange={handleFileChange} accept="image/*" />
                {!receiptPreview ? <div style={{color: '#00ff41'}}>üì∏ Upload Receipt</div> : <img src={receiptPreview} style={{height: '80px'}} alt="Preview" />}
              </label>

              <button style={styles.enrollBtn} onClick={handleSubmitMission} disabled={isSubmitting}>
                {isSubmitting ? "TRANSMITTING..." : "SUBMIT MISSION"}
              </button>
            </div>
          </div>
        </div>
      )}

      {/* STATUS CHECK MODAL */}
      {showStatusModal && (
        <div style={styles.modalOverlay} onClick={() => { setShowStatusModal(false); setStatusResult(null); }}>
          <div style={styles.modalContent} onClick={e => e.stopPropagation()}>
            <div style={styles.modalMain}>
              <h3 style={{ color: '#00ff41' }}>CHECK STATUS</h3>
              <input style={styles.input} placeholder="ENTER PHONE NUMBER" onChange={e => setCheckPhone(e.target.value)} />
              <button style={styles.enrollBtn} onClick={checkMyStatus}>CHECK NOW</button>
              {statusResult && (
                <div style={styles.statusBox}>
                   <p>Course: {statusResult.title}</p>
                   <p>Status: <b style={{color: statusResult.status === 'pending' ? '#ffcc00' : '#00ff41'}}>{statusResult.status.toUpperCase()}</b></p>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

