import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Parse from 'parse';

const Home = () => {
  const navigate = useNavigate();
  const [courses, setCourses] = useState([
    { id: "linux", title: "Linux Admin", price: "150,000 MMK", duration: "2 Months", req: "Basic Computer,less than RAM 8 And 256GB", vidId: "yv_vPAtE_kQ", img: "https://images.unsplash.com/photo-1629654297299-c8506221ca97", detail: "Master Linux CLI, Server Management, and Security Auditing.", introDes: "Linux á€á€Šá€º server á€œá€±á€¬á€€á á€˜á€¯á€›á€„á€ºá€–á€¼á€…á€ºá€á€Šá€ºá‹ Enterprise Server á€™á€»á€¬á€¸ á€á€Šá€ºá€†á€±á€¬á€€á€ºá€•á€¯á€¶á€€á€­á€¯ á€œá€€á€ºá€á€½á€±á€·á€á€„á€ºá€€á€¼á€¬á€¸á€›á€™á€Šá€ºá‹", instructorName: "Dr. May Mi Ko Ko", instructorImg: "images/919888af5779d83aa33de5790bf7eb72.jpg" },
    { id: "cyber", title: "Cyber Security", price: "200,000 MMK", duration: "2 Months", req: "Network Basics,Basic Computer,less than RAM 8 And 256GB", vidId: "Z3_6qJ0pGno", img: "https://images.unsplash.com/photo-1550751827-4bd374c3f58b", detail: "Learn Threat Hunting, SOC Analysis, and Incident Response.", introDes: "Security Analyst á€á€…á€ºá€šá€±á€¬á€€á€ºá€–á€¼á€…á€ºá€–á€­á€¯á€· á€œá€­á€¯á€¡á€•á€ºá€á€²á€· SOC Operations á€™á€»á€¬á€¸á€€á€­á€¯ Lab á€™á€»á€¬á€¸á€–á€¼á€„á€·á€º á€œá€€á€ºá€á€½á€±á€·á€œá€±á€·á€€á€»á€„á€·á€ºá€›á€™á€Šá€ºá‹", instructorName: "U Kyaw Naing", instructorImg: "images/images (1).jpeg" },
