import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import Parse from 'parse';

const Admin = () => {
  const [isAdmin, setIsAdmin] = useState(false);
  const [adminPass, setAdminPass] = useState('');
  const [courseId, setCourseId] = useState('linux');
  const [formData, setFormData] = useState({ instructor: '', videoLink: '' });

  const [activeTab, setActiveTab] = useState('pending');
  const [dataList, setDataList] = useState([]);
  const [loading, setLoading] = useState(false);

  const checkPass = () => {
    if (adminPass === "snx123") {
      setIsAdmin(true);
    } else {
      alert("Access Denied! ❌");
    }
  };

  // Database ထဲက အချက်အလက်အားလုံးကို ခြွင်းချက်မရှိ ဆွဲထုတ်မည့် Function
  const fetchData = useCallback(async () => {
    setLoading(true);
    setDataList([]); // အဟောင်းတွေကို အရင်ရှင်းထုတ်မယ်
    try {
      let query;
      if (activeTab === 'users') {
        query = new Parse.Query(Parse.User);
      } else {
        query = new Parse.Query("Enrollments");
        query.equalTo("status", activeTab);
      }

      query.descending("createdAt");
      query.limit(1000); // လူအရေအတွက် အကန့်အသတ်မရှိအောင် မြှင့်ထားမယ်

      const results = await query.find();

      // အသစ်ရလာတဲ့ User/Enrollment အားလုံးကို State ထဲ ထည့်သွင်းမယ်
      setDataList(results);
      console.log(`Mission Success: Fetched ${results.length} agents.`);
    } catch (error) {
      console.error("Sync Error:", error);
    } finally {
      setLoading(false);
    }
  }, [activeTab]);

  useEffect(() => {
    if (isAdmin) {
      fetchData();

      // Real-time Update (Live Query)
      let subscription;
      const setupLiveQuery = async () => {
        try {
          const liveClass = activeTab === 'users' ? Parse.User : "Enrollments";
          const query = new Parse.Query(liveClass);
          subscription = await query.subscribe();

          subscription.on('create', () => fetchData());
          subscription.on('update', () => fetchData());
          subscription.on('enter', () => fetchData());
        } catch (e) {
          console.log("LiveQuery initializing...");
        }
      };

      setupLiveQuery();
      return () => { if(subscription) subscription.unsubscribe(); };
    }
  }, [isAdmin, activeTab, fetchData]);

  const handleApprove = async (objectId) => {
    try {
      const query = new Parse.Query("Enrollments");
      const enrollment = await query.get(objectId);
      enrollment.set("status", "approved");
      await enrollment.save();
      alert("AGENT AUTHORIZED! ✅");
      fetchData();
    } catch (err) {
      alert("Error: " + err.message);
    }
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    const Course = Parse.Object.extend("Classes");
    const query = new Parse.Query(Course);
    query.equalTo("courseId", courseId);
    try {
      let obj = await query.first();
      if (!obj) { alert("Class not found!"); return; }
      obj.set("instructor", formData.instructor);
      obj.set("videoLink", formData.videoLink);
      await obj.save();
      alert("Course Intel Updated! ✅");
    } catch (err) { alert(err.message); }
  };

  if (!isAdmin) return (
    <div style={styles.loginOverlay}>
      <h2 style={{color: '#00ff41', textShadow: '0 0 15px #00ff41'}}>[ SYSTEM ACCESS ]</h2>
      <div style={styles.loginBox}>
        <input 
          type="password" 
          placeholder="ENCRYPTION_KEY" 
          onChange={e => setAdminPass(e.target.value)} 
          style={styles.loginInput} 
        />
        <button onClick={checkPass} style={styles.loginBtn}>LOGIN</button>
      </div>
    </div>
  );

  return (
    <div style={styles.adminBody}>
      <div style={styles.header}>
        <h1 style={{color: '#00ff41', fontSize: '1.5rem', letterSpacing: '2px'}}>COMMAND_CENTER_2.0</h1>
        <Link to="/add-class" style={styles.addClassBtn}>+ CREATE_NEW_CLASS</Link>
      </div>

      <div style={styles.gridContainer}>
        {/* SETTINGS FORM */}
        <section style={styles.section}>
          <h3 style={styles.sectionTitle}>MODIFY_MISSION_DATA</h3>
          <form onSubmit={handleUpdate} style={styles.form}>
            <select onChange={e => setCourseId(e.target.value)} style={styles.input}>
              <option value="linux">Linux Admin</option>
              <option value="cyber">Cyber Security</option>
              <option value="hacking">World Hacking</option>
            </select>
            <input placeholder="Instructor Name" onChange={e => setFormData({...formData, instructor: e.target.value})} style={styles.input} />
            <input placeholder="YouTube ID" onChange={e => setFormData({...formData, videoLink: e.target.value})} style={styles.input} />
            <button type="submit" style={styles.updateBtn}>SUBMIT UPDATE</button>
          </form>
        </section>

        {/* LISTS SECTION */}
        <section style={styles.section}>
          <div style={styles.tabHeader}>
            <button onClick={() => setActiveTab('pending')} style={activeTab === 'pending' ? styles.activeTab : styles.tab}>PENDING</button>
            <button onClick={() => setActiveTab('approved')} style={activeTab === 'approved' ? styles.activeTab : styles.tab}>APPROVED</button>
            <button onClick={() => setActiveTab('users')} style={activeTab === 'users' ? styles.activeTab : styles.tab}>ALL_USERS</button>
          </div>

          <div style={styles.listWrapper}>
            {loading && dataList.length === 0 ? (
              <p style={{color: '#00ff41', textAlign: 'center'}}>SYNCING WITH SATELLITE...</p>
            ) : (
              <div style={styles.scrollArea}>
                {dataList.length === 0 ? (
                  <p style={{textAlign: 'center', color: '#444', marginTop: '20px'}}>DATABASE_EMPTY</p>
                ) : (
                  dataList.map((item, idx) => (
                    <div key={item.id} style={styles.card}>
                      <div style={{display: 'flex', gap: '15px', alignItems: 'center'}}>
                        <div style={styles.idxBadge}>{idx + 1}</div>
                        <div>
                          <div style={{color: '#00ff41', fontWeight: 'bold'}}>
                            {item.get('username') || item.get('studentName')}
                          </div>
                          <div style={{fontSize: '12px', color: '#8892b0'}}>
                            {activeTab === 'users' ? item.get('email') : `Ph: ${item.get('phoneNumber')}`}
                          </div>
                          <div style={{fontSize: '10px', color: '#444', marginTop: '3px'}}>
                            {item.get('courseTitle') ? `Target: ${item.get('courseTitle')}` : 'Base Access Only'} | {item.createdAt.toLocaleString()}
                          </div>
                        </div>
                      </div>
                      {activeTab === 'pending' && (
                        <button onClick={() => handleApprove(item.id)} style={styles.approveBtn}>AUTHORIZE</button>
                      )}
                    </div>
                  ))
                )}
              </div>
            )}
            <div style={styles.footerInfo}>Live Records: {dataList.length}</div>
          </div>
        </section>
      </div>
    </div>
  );
};

