import React, { useState } from 'react';
import Parse from 'parse';

const Admin = () => {
  const [courseId, setCourseId] = useState('linux');
  const [zoomLink, setZoomLink] = useState('');
  const [videoLink, setVideoLink] = useState('');
  const [schedule, setSchedule] = useState('');
  const [instructor, setInstructor] = useState('');

  const handleUpdate = async (e) => {
    e.preventDefault();
    const Course = Parse.Object.extend("Classes");
    const query = new Parse.Query(Course);
    query.equalTo("courseId", courseId);

    try {
      let obj = await query.first();
      if (!obj) obj = new Course(); // á€™á€›á€¾á€­á€›á€„á€º á€¡á€žá€…á€ºá€†á€±á€¬á€€á€ºá€™á€šá€º

      obj.set("courseId", courseId);
      obj.set("zoomLink", zoomLink);
      obj.set("videoLink", videoLink);
      obj.set("schedule", schedule);
      obj.set("instructor", instructor);

      await obj.save();
      alert("System Updated Successfully! âœ…");
    } catch (error) {
      alert("Error: " + error.message);
    }
  };

  return (
    <div style={{ backgroundColor: '#0a192f', minHeight: '100vh', color: '#00ff41', padding: '50px' }}>
      <h1>ðŸ›  Admin Control Center</h1>
      <form onSubmit={handleUpdate} style={styles.form}>
        <label>Select Class:</label>
        <select onChange={(e) => setCourseId(e.target.value)} style={styles.input}>
          <option value="linux">Linux Administration</option>
          <option value="cyber">Cyber Security</option>
          <option value="hacking">Real World Hacking</option>
        </select>

        <input type="text" placeholder="Instructor Name" style={styles.input} onChange={(e)=>setInstructor(e.target.value)} />
        <input type="text" placeholder="Zoom Link" style={styles.input} onChange={(e)=>setZoomLink(e.target.value)} />
        <input type="text" placeholder="YouTube Video Link" style={styles.input} onChange={(e)=>setVideoLink(e.target.value)} />
        <input type="text" placeholder="Schedule (e.g. Mon-Wed 7PM)" style={styles.input} onChange={(e)=>setSchedule(e.target.value)} />

        <button type="submit" style={styles.btn}>UPDATE SYSTEM</button>
      </form>
    </div>
  );
};

const styles = {
  form: { display: 'flex', flexDirection: 'column', maxWidth: '500px', gap: '15px', background: '#112240', padding: '30px', borderRadius: '10px' },
  input: { padding: '12px', background: '#0a192f', border: '1px solid #00ff41', color: '#fff', borderRadius: '5px' },
  btn: { padding: '15px', background: '#00ff41', color: '#0a192f', fontWeight: 'bold', border: 'none', cursor: 'pointer', borderRadius: '5px' }
};

export default Admin;
