import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  const initialCourses = [
    { 
      id: "linux", title: "Linux Admin", price: "55,000 MMK", duration: "2 Months", 
      req: "Basic Computer", vidId: "yv_vPAtE_kQ",
      img: "https://images.unsplash.com/photo-1629654297299-c8506221ca97?q=80&w=1000", 
      detail: "Master Linux CLI, Server Management, and Security Auditing." 
    },
    { 
      id: "cyber", title: "Cyber Security", price: "75,000 MMK", duration: "3 Months", 
      req: "Network Basics", vidId: "Z3_6qJ0pGno",
      img: "https://images.unsplash.com/photo-1550751827-4bd374c3f58b?q=80&w=1000", 
      detail: "Learn Threat Hunting, SOC Analysis, and Incident Response." 
    },
    { 
      id: "net", title: "Networking", price: "45,000 MMK", duration: "2 Months", 
      req: "None", vidId: "09Z_yK_hS3k",
      img: "https://images.unsplash.com/photo-1544197150-b99a580bb7a8?q=80&w=1000", 
      detail: "Deep dive into CCNA level Networking and Protocols." 
    },
    { 
      id: "py_basic", title: "Python Basic", price: "40,000 MMK", duration: "1.5 Months", 
      req: "Logical Thinking", vidId: "mX4Z0D_vL_A",
      img: "https://images.unsplash.com/photo-1526374965328-7f61d4dc18c5?q=80&w=1000", 
      detail: "Fundamentals of Programming and Automation with Python 3." 
    },
    { 
      id: "py_adv", title: "Python Adv", price: "60,000 MMK", duration: "2 Months", 
      req: "Python Basics", vidId: "X_9e6XmS-Y0",
      img: "https://images.unsplash.com/photo-1515879218367-8466d910aaa4?q=80&w=1000", 
      detail: "Deep dive into OOP, Web Frameworks, and Data Analysis." 
    },
    { 
      id: "hacking", title: "World Hacking", price: "95,000 MMK", duration: "4 Months", 
      req: "Linux & Networking", vidId: "f9p233_rMSc",
      img: "https://images.unsplash.com/photo-1563986768609-322da13575f3?q=80&w=1000", 
      detail: "Real-world Pentesting, Bug Bounty, and Exploitation Techniques." 
    }
  ];

  const [courses, setCourses] = useState(initialCourses);
  const [selectedCourse, setSelectedCourse] = useState(null);

  const handleNext = () => {
    const newItems = [...courses];
    newItems.push(newItems.shift());
    setCourses(newItems);
  };

  const handlePrev = () => {
    const newItems = [...courses];
    newItems.unshift(newItems.pop());
    setCourses(newItems);
  };

  const handleCardClick = (index) => {
    if (index >= 2) {
      // ·Ää·Ä¨·Äò·ÄÄ·Ä∫·ÄÄ ·ÄÄ·Äê·Ä∫·Äê·ÄΩ·Ä±·ÄÄ·Ä≠·ÄØ ·Äî·Äæ·Ä≠·Äï·Ä∫·Äõ·ÄÑ·Ä∫ Next ·Äû·ÄΩ·Ä¨·Ä∏·Äô·Äö·Ä∫
      handleNext();
    } else if (index === 0) {
      // ·Äõ·Äæ·Ä±·Ä∑·ÄÜ·ÄØ·Ä∂·Ä∏·ÄÄ ·Äï·ÄØ·Ä∂·Äõ·Ä≠·Äï·Ä∫·Äö·Ä±·Ä¨·ÄÑ·Ä∫·ÄÄ·Äê·Ä∫·ÄÄ·Ä≠·ÄØ ·Äî·Äæ·Ä≠·Äï·Ä∫·Äõ·ÄÑ·Ä∫ Prev ·Äû·ÄΩ·Ä¨·Ä∏·Äô·Äö·Ä∫
      handlePrev();
    }
  };

  return (
    <div style={styles.body}>
      <nav style={styles.nav}>
        <h2 style={{ color: '#00ff41', margin: 0, letterSpacing: '2px' }}>2 CYBER_SHIELD</h2>
        <div style={styles.navRight}>
          <Link to="/login" style={styles.loginBtn}>LOGIN</Link>
          <Link to="/register" style={styles.regBtn}>REGISTER</Link>
          <Link to="/register" style={styles.joinBtn}>JOIN</Link>
        </div>
      </nav>

      <div style={styles.sliderContainer}>
        <div style={styles.slide}>
          {courses.map((course, index) => (
            <div 
              key={course.id}
              onClick={() => handleCardClick(index)}
              style={{
                ...styles.item,
                backgroundImage: `url(${course.img})`,
                // ·ÄÅ·Äº·Ä±·Ä¨·ÄÄ·Ä∫·ÄÅ·ÄØ·Äú·ÄØ·Ä∂·Ä∏·Äï·Ä±·Ä´·Ä∫·Ä°·Ä±·Ä¨·ÄÑ·Ä∫ Width ·Äî·Ä≤·Ä∑ Left ·ÄÄ·Ä≠·ÄØ ·ÄÅ·Äª·Ä≠·Äî·Ä∫·Ää·Äæ·Ä≠·Äë·Ä¨·Ä∏·Äï·Ä´·Äê·Äö·Ä∫
                width: index < 2 ? '100%' : '140px',
                height: index < 2 ? '100%' : '200px',
                left: index < 2 ? '0' : `calc(45% + ${(index - 2) * 155}px)`,
                top: index < 2 ? '0' : '65%',
                opacity: 1, // ·Ä°·ÄÄ·ÄØ·Äî·Ä∫·Äï·Ä±·Ä´·Ä∫·ÄÖ·Ä±·ÄÅ·Äª·ÄÑ·Ä∫·Äú·Ä≠·ÄØ·Ä∑ ·ÅÅ ·Äï·Ä±·Ä∏·Äë·Ä¨·Ä∏·Äï·Ä´·Äê·Äö·Ä∫
                zIndex: index < 2 ? 1 : 10,
                borderRadius: index < 2 ? '0' : '12px',
                cursor: index >= 2 ? 'pointer' : 'default',
                boxShadow: index < 2 ? 'none' : '0 10px 30px rgba(0,0,0,0.8)'
              }}
            >
