import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  return (
    <div style={{ backgroundColor: '#0a192f', minHeight: '100vh', color: '#ccd6f6', fontFamily: 'monospace' }}>
      {/* Hero Section */}
      <header style={{ height: '80vh', display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', textAlign: 'center', padding: '0 20px' }}>
        <h1 style={{ fontSize: '4rem', color: '#00ff41', marginBottom: '10px' }}>CYBER_SHIELD ACADEMY</h1>
        <p style={{ fontSize: '1.2rem', color: '#8892b0', maxWidth: '600px' }}>
          Learn the art of Ethical Hacking, Linux, and Network Security from the masters.
        </p>
        <div style={{ marginTop: '30px', display: 'flex', gap: '20px' }}>
          <Link to="/register" style={styles.primaryBtn}>GET STARTED</Link>
          <Link to="/login" style={styles.secondaryBtn}>STUDENT LOGIN</Link>
        </div>
      </header>

      {/* Course Preview */}
      <section style={{ padding: '50px 10%', background: '#112240' }}>
        <h2 style={{ color: '#00ff41', textAlign: 'center' }}>AVAILABLE TRACKS</h2>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '20px', marginTop: '40px' }}>
          {["Linux Admin", "Cyber Security", "Networking", "Python", "Real World Hacking"].map((course) => (
            <div key={course} style={{ padding: '20px', border: '1px solid #00ff41', borderRadius: '5px', textAlign: 'center' }}>
              <h3 style={{ color: '#00ff41' }}>{course}</h3>
              <p style={{ fontSize: '14px' }}>Expert-led training via Zoom.</p>
            </div>
          ))}
        </div>
      </section>

      {/* Footer */}
      <footer style={{ padding: '30px', textAlign: 'center', fontSize: '12px', color: '#444' }}>
        &copy; 2026 CyberShield Academy. All Rights Reserved.
      </footer>
    </div>
  );
};

const styles = {
  primaryBtn: { padding: '15px 30px', background: '#00ff41', color: '#0a192f', textDecoration: 'none', fontWeight: 'bold', borderRadius: '5px' },
  secondaryBtn: { padding: '15px 30px', border: '1px solid #00ff41', color: '#00ff41', textDecoration: 'none', borderRadius: '5px' }
};

export default Home;
