import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Parse from 'parse';

const Admin = () => {
  const [isAdmin, setIsAdmin] = useState(false);
  const [adminPass, setAdminPass] = useState('');
  const [courseId, setCourseId] = useState('linux');
  const [formData, setFormData] = useState({ instructor: '', zoomLink: '', videoLink: '', schedule: '' });

  const checkPass = () => {
    if (adminPass === "snx_admin_2026") setIsAdmin(true);
    else alert("Wrong Password!");
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    const Course = Parse.Object.extend("Classes");
    const query = new Parse.Query(Course);
    query.equalTo("courseId", courseId);
    try {
      let obj = await query.first();
      if (!obj) { alert("Class not found! Add it first."); return; }
      obj.set("instructor", formData.instructor);
      obj.set("zoomLink", formData.zoomLink);
      obj.set("videoLink", formData.videoLink);
      obj.set("schedule", formData.schedule);
      await obj.save();
      alert("Updated! ✅");
    } catch (err) { alert(err.message); }
  };

  if (!isAdmin) return (
    <div style={{background: '#0a192f', height: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'column'}}>
      <h2 style={{color: '#00ff41'}}>ADMIN ACCESS</h2>
      <input type="password" onChange={e => setAdminPass(e.target.value)} style={{margin: '10px', padding: '10px'}} />
      <button onClick={checkPass} style={{background: '#00ff41', padding: '10px 20px', border: 'none'}}>LOGIN</button>
    </div>
  );

  return (
    <div style={{background: '#0a192f', minHeight: '100vh', color: '#00ff41', padding: '50px'}}>
      <div style={{display: 'flex', justifyContent: 'space-between'}}>
        <h1>ADMIN PANEL</h1>
        <Link to="/add-class" style={{color: '#00ff41', border: '1px solid #00ff41', padding: '10px', textDecoration: 'none'}}>+ ADD CLASS</Link>
      </div>
      <form onSubmit={handleUpdate} style={{display: 'flex', flexDirection: 'column', gap: '15px', maxWidth: '400px', background: '#112240', padding: '20px'}}>
        <select onChange={e => setCourseId(e.target.value)} style={{padding: '10px'}}>
          <option value="linux">Linux</option>
          <option value="cyber">Cyber</option>
          <option value="hacking">Hacking</option>
        </select>
        <input placeholder="Instructor" onChange={e => setFormData({...formData, instructor: e.target.value})} style={{padding: '10px'}} />
        <input placeholder="Video ID" onChange={e => setFormData({...formData, videoLink: e.target.value})} style={{padding: '10px'}} />
        <button type="submit" style={{padding: '10px', background: '#00ff41'}}>UPDATE</button>
      </form>
    </div>
  );
};

export default Admin;
