import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Parse from 'parse';

const Login = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleRegister = async () => {
    try {
      const user = new Parse.User();
      user.set("username", username);
      user.set("password", password);
      await user.signUp();
      alert("Account Created! You can login now.");
    } catch (error) {
      alert("Error: " + error.message);
    }
  };

  const handleLogin = async () => {
    try {
      await Parse.User.logIn(username, password);
      alert("Access Granted!");
      navigate('/dashboard'); // Dashboard ကို လွှတ်မယ်
    } catch (error) {
      alert("Access Denied: " + error.message);
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.loginBox}>
        <h2 style={{ letterSpacing: '3px' }}>SYSTEM ACCESS</h2>
        <input 
          type="text" placeholder="Hacker Name (Username)" 
          style={styles.input} onChange={(e) => setUsername(e.target.value)} 
        />
        <input 
          type="password" placeholder="Passcode" 
          style={styles.input} onChange={(e) => setPassword(e.target.value)} 
        />

        <div style={{ display: 'flex', gap: '10px' }}>
          <button onClick={handleLogin} style={styles.loginBtn}>LOGIN</button>
          <button onClick={handleRegister} style={styles.regBtn}>REGISTER</button>
        </div>

        <p onClick={() => navigate('/')} style={styles.backLink}>[ Terminate Connection ]</p>
      </div>
    </div>
  );
};

const styles = {
  container: { backgroundColor: '#0a192f', height: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center' },
  loginBox: { background: '#112240', padding: '40px', borderRadius: '15px', border: '1px solid #00ff41', textAlign: 'center', width: '350px', boxShadow: '0 0 20px #00ff4144' },
  input: { width: '100%', padding: '12px', margin: '10px 0', background: '#0a192f', border: '1px solid #233554', color: '#00ff41', borderRadius: '5px', boxSizing: 'border-box' },
  loginBtn: { flex: 1, padding: '12px', background: '#00ff41', border: 'none', color: '#0a192f', fontWeight: 'bold', cursor: 'pointer', borderRadius: '5px' },
  regBtn: { flex: 1, padding: '12px', background: 'transparent', border: '1px solid #00ff41', color: '#00ff41', fontWeight: 'bold', cursor: 'pointer', borderRadius: '5px' },
  backLink: { fontSize: '12px', color: '#8892b0', cursor: 'pointer', marginTop: '25px', display: 'block' }
};

export default Login;
